int increment_by_n(int num) {
	int result = 0;
	int i;
	for (i = 1; i <= num; i++) {
		result += i;
	}
	return result;
}

int sum_of_digits(int n) {
	int sum = 0;
	while (n != 0) {
		sum += n % 10;
		n /= 10;
	}
	return sum;
}

int square_sum(int n) {
	int sum = 0;
	int i;
	for (i = 1; i <= n; i++) {
		sum += i * i;
	}
	return sum;
}

int sum_of_cubes(int n) {
	int sum = 0;
	int i;
	for (i = 1; i <= n; i++) {
		sum += i * i * i;
	}
	return sum;
}

int factorial(int n) {
	int result = 1;
	int i;
	for (i = 2; i <= n; i++) {
		result *= i;
	}
	return result;
}

int sum_up_to_ten() {
	int sum = 0;
	int i;
	for (i = 1; i <= 10; i++) {
		sum += i;
	}
	return sum;
}

int sum_of_even_numbers() {
	int sum = 0;
	int i;
	for (i = 2; i <= 10; i += 2) {
		sum += i;
	}
	return sum;
}
