int factorial(int n) {
	int result = 1;
	while (n > 1) {
		result *= n;
		n--;
	}
	return result;
}

int sum_of_digits(int n) {
	int total = 0;
	while (n != 0) {
		total += n % 10;
		n /= 10;
	}
	return total;
}

int is_prime(int n) {
	if (n <= 1) {
		return 0;
	}
	int i = 2;
	while (i * i <= n) {
		if (n % i == 0) {
			return 0;
		}
		i++;
	}
	return 1;
}

int reverse_digits(int n) {
	int reversed_num = 0;
	while (n != 0) {
		int digit = n % 10;
		reversed_num = reversed_num * 10 + digit;
		n /= 10;
	}
	return reversed_num;
}

int judged_by_zero(int n) {
	if (n == 0) {
		return 1;
	} else {
		return 0;
	}
}
