def countdown(n):
	while n >= 0:
		print(n)
		n -= 1

def reverse_string(s):
	result = ''
	while s:
		result += s[-1]
		s = s[:-1]
	return result

def find_maximum(lst):
	max_value = float('-inf')
	while lst:
		value = lst.pop()
		if value > max_value:
			max_value = value
	return max_value

def sum_of_digits(n):
	total = 0
	while n > 0:
		total += n % 10
		n //= 10
	return total

def fibonacci(n):
	a, b = 0, 1
	while n > 0:
		print(a)
		a, b = b, a + b
		n -= 1

def is_prime(n):
	if n <= 1:
		return False
	i = 2
	while i * i <= n:
		if n % i == 0:
			return False
		i += 1
	return True
